/**
 * Custom Service Worker for Push Notifications and Background Sync
 * This file is loaded by the service worker generated by next-pwa
 */

// Listen for push events
self.addEventListener('push', (event) => {
  console.log('Push event received:', event);

  let notificationData = {
    title: 'Karasu Emlak',
    body: 'Yeni bildiriminiz var!',
    icon: '/logo-icon.svg',
    badge: '/logo-icon.svg',
    tag: 'karasu-emlak-notification',
    requireInteraction: false,
    data: {},
  };

  // Parse push data if available
  if (event.data) {
    try {
      const data = event.data.json();
      notificationData = {
        ...notificationData,
        ...data,
      };
    } catch (e) {
      // If not JSON, use as text
      notificationData.body = event.data.text() || notificationData.body;
    }
  }

  // Show notification
  event.waitUntil(
    self.registration.showNotification(notificationData.title, {
      body: notificationData.body,
      icon: notificationData.icon,
      badge: notificationData.badge,
      tag: notificationData.tag,
      data: notificationData.data,
      requireInteraction: notificationData.requireInteraction,
      actions: notificationData.actions || [],
    })
  );
});

// Handle notification clicks
self.addEventListener('notificationclick', (event) => {
  console.log('Notification clicked:', event);

  event.notification.close();

  const notificationData = event.notification.data || {};
  const urlToOpen = notificationData.url || '/';

  event.waitUntil(
    clients
      .matchAll({
        type: 'window',
        includeUncontrolled: true,
      })
      .then((clientList) => {
        // Check if there's already a window open
        for (let i = 0; i < clientList.length; i++) {
          const client = clientList[i];
          if (client.url === urlToOpen && 'focus' in client) {
            return client.focus();
          }
        }

        // Open new window if none exists
        if (clients.openWindow) {
          return clients.openWindow(urlToOpen);
        }
      })
  );
});

// Handle notification close
self.addEventListener('notificationclose', (event) => {
  console.log('Notification closed:', event);
  // Track notification dismissal if needed
});

// Background Sync for offline form submissions
self.addEventListener('sync', (event) => {
  console.log('Background sync event:', event.tag);

  if (event.tag.startsWith('sync-')) {
    event.waitUntil(syncPendingTasks());
  }
});

// Process pending sync tasks
async function syncPendingTasks() {
  try {
    // Get tasks from IndexedDB or fetch from API
    const response = await fetch('/api/sync/process', {
      method: 'POST',
    });

    if (!response.ok) {
      throw new Error('Sync failed');
    }

    console.log('✅ Background sync completed');
  } catch (error) {
    console.error('❌ Background sync failed:', error);
    throw error; // Retry sync
  }
}

// Periodic background sync (if supported)
if ('periodicSync' in self.registration) {
  self.addEventListener('periodicsync', (event) => {
    if (event.tag === 'content-sync') {
      event.waitUntil(syncPendingTasks());
    }
  });
}
